mapper.update:
UPDATE files
SET parent_id = #{parentId}, name = #{name}, size = #{size}, hash_value = #{hashValue}, file_type = #{fileType}, storage_node_id = #{storageNodeId}, storage_path = #{storagePath}, mime_type = #{mimeType}, is_folder = #{isFolder}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM files
WHERE user_id = #{userId} AND deleted_at IS NULL

    AND name LIKE CONCAT('%', #{request.keyword}, '%')


    AND parent_id = #{request.folderId}


    AND mime_type LIKE CONCAT(#{request.fileType}, '%')


    AND size >= #{request.minSize}


    AND size <= #{request.maxSize}


    AND created_at >= #{request.startDate}


    AND created_at <= #{request.endDate}



        ORDER BY name

            DESC



        ORDER BY size

            DESC



        ORDER BY created_at DESC



    LIMIT #{request.pageSize} OFFSET #{request.offset}
